INSERT INTO news_articles (title, content, summary, image_url, video_url, source_url, source_name, published_at, created_at, is_auto_generated) VALUES
('Boston Dynamics представила нового гуманоидного робота Atlas', 'Компания Boston Dynamics анонсировала полностью электрическую версию своего гуманоидного робота Atlas. Новая модель демонстрирует невероятную гибкость и способность двигаться способами, недоступными человеку. Atlas нового поколения полностью отказался от гидравлики в пользу электрических приводов, что делает его более эффективным и тихим. Робот может поднимать тяжелые предметы, преодолевать препятствия и выполнять сложные акробатические движения.', 'Boston Dynamics представила электрическую версию Atlas с улучшенной гибкостью', '/spot-robot.png', '', 'https://www.bostondynamics.com', 'Boston Dynamics', unixepoch('now'), unixepoch('now'), 0),

('Tesla Optimus Gen 2 показал впечатляющие улучшения', 'Tesla представила второе поколение своего гуманоидного робота Optimus. Новая версия на 30% легче, движется на 30% быстрее и имеет улучшенные манипуляторы с тактильными датчиками на пальцах. Илон Маск заявил, что Optimus сможет выполнять опасные, повторяющиеся и скучные задачи уже в ближайшие годы. Робот демонстрирует способность деликатно обращаться с хрупкими предметами благодаря новым сенсорам.', 'Tesla Optimus Gen 2: на 30% быстрее и легче с улучшенными манипуляторами', '/humanoid-service-robot.png', '', 'https://www.tesla.com', 'Tesla', unixepoch('now'), unixepoch('now'), 0),

('Роботы Amazon доставляют более миллиарда посылок', 'Складские роботы Amazon преодолели важную веху, обработав более миллиарда посылок. Компания продолжает инвестировать в автоматизацию своих центров выполнения заказов, используя роботов Proteus, которые могут безопасно перемещаться среди людей без необходимости в защитных барьерах. Это значительно ускоряет процесс сортировки и доставки товаров.', 'Amazon роботы обработали миллиард посылок', '/cobot-arm.png', '', 'https://www.aboutamazon.com', 'Amazon', unixepoch('now', '-1 day'), unixepoch('now', '-1 day'), 0),

('Сельскохозяйственные дроны революционизируют фермерство', 'Новое поколение сельскохозяйственных дронов позволяет фермерам более эффективно управлять своими полями. DJI Agras T40 может обрабатывать до 20 гектаров в час, используя точное распыление и картографирование полей с помощью многоспектральных камер. Технология позволяет экономить до 90% воды и 30% удобрений.', 'Новые агро-дроны повышают эффективность земледелия', '/agricultural-drone.png', '', 'https://ag.dji.com', 'DJI Agriculture', unixepoch('now', '-2 days'), unixepoch('now', '-2 days'), 0),

('iRobot представил новый Roomba с ИИ', 'Компания iRobot анонсировала новую модель робота-пылесоса Roomba, оснащенную передовым искусственным интеллектом. Робот может распознавать более 80 типов объектов и адаптировать стратегию уборки в зависимости от типа пола и загрязнения. Новая система навигации позволяет строить более точные карты помещений.', 'Новый Roomba с ИИ распознает 80+ типов объектов', '/robot-vacuum.png', '', 'https://www.irobot.com', 'iRobot', unixepoch('now', '-3 days'), unixepoch('now', '-3 days'), 0);

