INSERT INTO robot_catalog (name, category, description, price, official_website, image_url, specifications, created_at, updated_at) VALUES
('Boston Dynamics Spot', 'Промышленная инспекция, безопасность, исследования', 'Четвероногий робот для автономной навигации в сложных условиях. Spot оснащен 360-градусным восприятием окружающей среды, может преодолевать лестницы и неровную местность. Идеален для инспекций в опасных или труднодоступных местах.', '$74,500', 'https://www.bostondynamics.com/products/spot', '/spot-robot.png', 'Грузоподъемность: 14 кг, Время работы: 90 минут, Скорость: 1.6 м/с, Вес: 32.7 кг', unixepoch('now'), unixepoch('now')),

('Universal Robots UR10', 'Производство, упаковка, паллетирование', 'Коллаборативный робот (коботт) для совместной работы с людьми. Грузоподъемность 10 кг, радиус действия 1300 мм. Легко программируется и перенастраивается для различных задач. Безопасен для работы рядом с людьми без защитных ограждений.', '$45,000', 'https://www.universal-robots.com/products/ur10-robot/', '/cobot-arm.png', 'Грузоподъемность: 10 кг, Радиус действия: 1300 мм, 6 осей, Повторяемость: ±0.1 мм', unixepoch('now'), unixepoch('now')),

('DJI Agras T30', 'Сельское хозяйство', 'Сельскохозяйственный дрон для опрыскивания полей с баком на 30 литров. Оснащен системой точного позиционирования RTK и может покрывать до 16 гектаров в час. Автоматическое планирование маршрута и обход препятствий.', '$15,000', 'https://www.dji.com/agras-t30', '/agricultural-drone.png', 'Бак: 30 л, Производительность: 16 га/ч, Время полета: 18 мин, Вес: 38.5 кг', unixepoch('now'), unixepoch('now')),

('iRobot Roomba j7+', 'Домашняя уборка', 'Робот-пылесос с функцией распознавания препятствий и автоматической очистки контейнера. Использует ИИ для определения и избегания проводов, обуви и других объектов. База Clean Base автоматически опорожняет контейнер на 60 дней.', '$799', 'https://www.irobot.com/roomba/j-series', '/robot-vacuum.png', 'Время работы: 75 мин, Мощность всасывания: 10x AeroForce, Wi-Fi управление, Контейнер: 0.4 л', unixepoch('now'), unixepoch('now')),

('SoftBank Pepper', 'Сервис, образование, развлечения', 'Гуманоидный робот для взаимодействия с людьми. Pepper может распознавать эмоции, общаться голосом и демонстрировать информацию на планшете. Используется в магазинах, отелях, школах и на выставках для приветствия посетителей и предоставления информации.', '$25,000', 'https://www.softbankrobotics.com/emea/en/pepper', '/humanoid-service-robot.png', 'Рост: 120 см, Батарея: 12 часов, Датчики: 20+ включая камеры 3D, Вес: 28 кг', unixepoch('now'), unixepoch('now'));

-- FAQ Items
INSERT INTO faq_items (question, answer, category, "order", created_at, updated_at) VALUES
('Что такое робототехника?', 'Робототехника — это междисциплинарная область науки и техники, объединяющая инженерию, компьютерные науки, механику и электронику для создания роботов. Роботы — это автоматизированные машины, способные воспринимать окружающую среду, принимать решения и выполнять физические задачи с различной степенью автономности.', 'Основы', 1, unixepoch('now'), unixepoch('now')),

('Какие типы роботов существуют?', 'Основные типы роботов: 1) Промышленные роботы (манипуляторы для производства), 2) Сервисные роботы (для сферы услуг), 3) Медицинские роботы (хирургические и реабилитационные), 4) Мобильные роботы (AGV, дроны), 5) Коллаборативные роботы (коботы для работы с людьми), 6) Гуманоидные роботы (человекоподобные), 7) Домашние роботы (пылесосы, газонокосилки).', 'Основы', 2, unixepoch('now'), unixepoch('now')),

('Сколько стоит промышленный робот?', 'Стоимость промышленного робота варьируется от $25,000 для простых моделей с небольшой грузоподъемностью до $400,000+ для сложных систем с дополнительным оборудованием (захваты, системы технического зрения, системы безопасности). Коллаборативные роботы (коботы) обычно стоят $30,000-$50,000.', 'Покупка', 3, unixepoch('now'), unixepoch('now')),

('Какое образование нужно для работы в робототехнике?', 'Для работы в робототехнике обычно требуется степень бакалавра или магистра в области: инженерии (мехатроника, машиностроение, электротехника), компьютерных наук, или смежных дисциплин. Важны знания программирования (C++, Python, ROS), механики, электроники, теории управления и машинного обучения. Многие специалисты проходят дополнительные курсы и сертификации.', 'Образование', 4, unixepoch('now'), unixepoch('now')),

('Безопасны ли роботы для работы с людьми?', 'Современные коллаборативные роботы (коботы) разработаны специально для безопасной работы рядом с людьми. Они оснащены датчиками силы и крутящего момента, системами обнаружения столкновений и автоматической остановкой при контакте. Однако промышленные роботы требуют защитных ограждений и систем безопасности. Все роботы должны соответствовать международным стандартам безопасности ISO 10218 и ISO/TS 15066.', 'Безопасность', 5, unixepoch('now'), unixepoch('now'));

-- DIY Projects
INSERT INTO diy_projects (title, description, content, difficulty, image_url, materials, steps, created_at, updated_at) VALUES
('Arduino робот-манипулятор', 'Создайте простой 4-осевой манипулятор используя Arduino и сервоприводы. Этот проект отлично подходит для изучения основ робототехники и управления сервоприводами.', 'Манипулятор может захватывать и перемещать легкие объекты, управляется джойстиком. Примерное время сборки: 6-8 часов. Стоимость компонентов: $80-100.', 'Средний', '/diy-robot-arm.png', 'Arduino Uno, 4x сервопривода MG996R, джойстик модуль, блок питания 5V 3A, каркас из фанеры или пластика, провода', '1. Соберите механическую конструкцию манипулятора из фанеры или распечатайте детали на 3D-принтере\n2. Установите сервоприводы в основание, плечо, предплечье и захват\n3. Подключите сервоприводы к Arduino: основание - pin 9, плечо - pin 10, предплечье - pin 11, захват - pin 6\n4. Подключите джойстик к аналоговым пинам A0-A3\n5. Загрузите код управления через Arduino IDE\n6. Откалибруйте диапазон движения каждого сервопривода\n7. Протестируйте управление джойстиком', unixepoch('now'), unixepoch('now')),

('Линейный следящий робот', 'Базовый проект для начинающих - робот, следующий по черной линии. Отличный первый проект для понимания принципов автономного управления и работы с датчиками.', 'Робот использует инфракрасные датчики для обнаружения линии и корректировки траектории. Примерное время сборки: 3-4 часа. Стоимость: $30-40.', 'Начальный', '/line-follower-robot.png', 'Arduino Nano, 2x мотора DC, драйвер моторов L293D, 3x ИК датчика линии (TCRT5000), шасси робота, 2 колеса + опорное колесо, батарея 9V, провода', '1. Соберите шасси робота и установите моторы\n2. Установите ИК датчики снизу (слева, центр, справа) на расстоянии 5-8 мм от земли\n3. Подключите моторы через драйвер L293D к пинам 5,6 и 9,10\n4. Подключите датчики к пинам 2, 3, 4\n5. Загрузите код следования линии\n6. Откалибруйте чувствительность датчиков\n7. Нарисуйте черную линию на белом фоне шириной 2-3 см\n8. Настройте PID-регулятор для плавного движения', unixepoch('now'), unixepoch('now')),

('Робот-пылесос на Raspberry Pi', 'Создайте простой автономный робот для уборки с навигацией на базе Raspberry Pi. Проект включает работу с Linux, ROS, и датчиками.', 'Робот может строить карту помещения и планировать маршрут уборки. Примерное время сборки: 15-20 часов. Стоимость: $150-200.', 'Продвинутый', '/diy-cleaning-robot.png', 'Raspberry Pi 4, моторы DC с энкодерами, 3x ультразвуковые датчики HC-SR04, драйвер моторов L298N, аккумулятор LiPo 11.1V 2200mAh, щетка и вентилятор, контейнер, корпус', '1. Соберите шасси робота с моторами и колесами\n2. Установите Raspberry Pi OS и ROS Noetic\n3. Подключите моторы через L298N к GPIO пинам\n4. Установите ультразвуковые датчики спереди и по бокам\n5. Напишите код для чтения датчиков расстояния\n6. Реализуйте алгоритм навигации (wall-following)\n7. Добавьте систему сбора мусора (вентилятор + контейнер)\n8. Настройте автономную работу с возвратом на базу\n9. Опционально: добавьте камеру для SLAM', unixepoch('now'), unixepoch('now'));
